 class CetakBarcode extends code128 { function Report($id) { $model = Periksa::model()->findByPk($id); $this->SetFont("\101\x72\x69\x61\x6c", '', 8); $this->Cell(2, 0.5, $model->idPasien->nama . "\x20\x28" . $model->idPasien->tgl_lahir . "\x29", 0.1, 0.1, "\x4c"); $this->Code128(0.11, 0.4, $model->nomor, 5, 0.9); $this->Ln(0.8); $this->Cell(2, 0.5, $model->nomor . "\x20\40\40\122\115\72" . $model->idPasien->no_rm . "\40\40" . "\133" . date("\x64\x2e\155\x2e\x79", strtotime($model->tanggal)) . "\x5d", 0, 0, "\114"); $this->Ln(0.3); $this->Cell(2, 0.5, Dokter::item($model->id_dokter), 0, 0, "\114"); } } ?>